// Prevents additional console window on Windows in release, DO NOT REMOVE!!
#![cfg_attr(not(debug_assertions), windows_subsystem = "windows")]

use std::sync::Mutex;

use tauri::{AppHandle, Manager, State};

// Learn more about Tauri commands at https://tauri.app/v1/guides/features/command
#[tauri::command]
fn greet(name: &str) -> String {
    format!("Hello, {}! You've been greeted from Rust!", name)
}

#[tauri::command]
fn toggle_devtools(app: AppHandle, app_state: State<Mutex<AppState>>) {
    #[cfg(debug_assertions)]
    {
        let mut state = app_state.lock().expect("App state mutex is poisoned");
        let window = app.get_webview_window("main").unwrap();
        if state.devtools_open {
            state.devtools_open = false;
            window.close_devtools();
        } else {
            state.devtools_open = true;
            window.open_devtools();
        }
    }
}

#[tauri::command]
fn plop() -> Result<String, String> {
    Ok(String::from("data:image/png;base64,"))
}

struct AppState {
    devtools_open: bool,
}

fn main() {
    tauri::Builder::default()
        .plugin(tauri_plugin_dialog::init())
        .plugin(tauri_plugin_shell::init())
        .invoke_handler(tauri::generate_handler![greet, plop, toggle_devtools,])
        .setup(|app| {
            app.manage(Mutex::new(AppState {
                devtools_open: false,
            }));

            Ok(())
        })
        .run(tauri::generate_context!())
        .expect("error while running tauri application");
}
